def game(wall):
    pygame.init()
    screen = pygame.display.set_mode((640, 480), 0, 32)
    screen.fill((255, 255, 255))
    line = []
    lines = []
    
    while True:
        for event in pygame.event.get():
            if event.type == QUIT:
                exit()
            if event.type == MOUSEMOTION:
                if pygame.mouse.get_pressed()[0]:
                    wall_scan(event.pos)
                    line.append(event.pos)
                    if len(line) > 1:
                        pygame.draw.lines(screen, (0, 255, 0), False, line, 3)
                if not pygame.mouse.get_pressed()[0]:
                    if len(line) > 1:
                        lines.append(line)
                    line = []

        draw(lines, (0, 255, 0), screen)
        draw(wall, (255, 0, 0), screen)
        pygame.display.update()


def point_colition(a, b, radius):
    x_coincidence = a[0] < b[0] + radius and a[0] > b[0] - radius 
    y_coincidence = a[1] < b[1] + radius and a[1] > b[1] - radius 
    return x_coincidence and y_coincidence


def wall_scan(event_pos):
    for vectors in wall:
        for point in vectors:
            if point_colition(event_pos, point, 9):
                print('LOOOSER!')


def draw(lines_list, color, screen):
    for drawed_line in lines_list:
        pygame.draw.lines(screen, color, False, drawed_line, 3)


game(wall)
